  class Queue {
    constructor() {
      this._callback = null;
      this._queue = [];
    }

    send(json) {
      this._queue.push(json);
    }

    receive(_callback) {
      this._callback = _callback;
    }

    flush(transport) {
      transport.receive(this._callback);
      this._queue.forEach((item) => transport.send(item));
    }
  }

